// Performance Monitoring
window.addEventListener('load',()=>{const perfData=performance.getEntriesByType('navigation')[0];console.log('Page Load Time:',perfData.loadEventEnd-perfData.loadEventStart);console.log('DOM Content Loaded:',perfData.domContentLoadedEventEnd-perfData.domContentLoadedEventStart);if('PerformanceObserver' in window){const observer=new PerformanceObserver(list=>{list.getEntries().forEach(entry=>{console.log(`${entry.name}:`,entry.value);if(entry.name==='LCP'){console.log('Largest Contentful Paint:',entry.value)}if(entry.name==='FID'){console.log('First Input Delay:',entry.value)}if(entry.name==='CLS'){console.log('Cumulative Layout Shift:',entry.value)}})});observer.observe({entryTypes:['largest-contentful-paint','first-input','layout-shift']})}});